import React, { useState } from 'react';
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import Card from 'react-bootstrap/Card'
import Container from 'react-bootstrap/Container';
import { Col } from 'react-bootstrap';

function Signup() {
    const [windowWidth, setWidth] = useState(window.innerWidth);
    const [windowHeight, setHeight] = useState(window.innerHeight - 56);
    const [contSize, setContSize] = useState(handleContSize());

    const handleContSize = () => {
        if (window.innerWidth <= 1100 && window.innerWidth >= 800)
            // this.setState({ size: window.innerWidth / 6 })
            setContSize(window.innerWidth / 6)
        else if (window.innerWidth > 1000)
            // this.setState({ size: window.innerWidth / 10 })
            setContSize(window.innerWidth / 6)
        else
            // this.setState({ size: window.innerWidth / 2.5 })
            // setColor('red')
            setContSize(window.innerWidth / 6)

    }
    // const [color, setColor] = useState('blue');

    const changeSize = () => {
        setWidth(window.innerWidth);
        setHeight(window.innerHeight - 56);
        // setColor('red')
    }

    window.addEventListener("resize", changeSize)

    return (
        <div style={{ backgroundColor: '#17223B', height: windowHeight, width: windowWidth }}>
            <Container style={{ backgroundColor: 'white', }} >
                <Form style={{ backgroundColor: 'red', }}>
                    <Form.Group controlId="email">
                        <Form.Control style={{}} type="text" placeholder='Username' />
                    </Form.Group>
                    {/* <Form.Group controlId="email">
                                <Form.Control type="email" placeholder="Enter email" />
                            </Form.Group>

                            <Form.Group controlId="phoneNumber">
                                <Form.Control type="email" placeholder='Phone number' />
                            </Form.Group>

                            <Form.Group controlId="password">
                                <Form.Control type="password" placeholder="Password" />
                            </Form.Group>

                            <Button variant="primary" type="submit">
                                Submit
                        </Button> */}
                </Form >
            </Container >
        </div >
    );
}

export default Signup;
