import React, { useEffect, useState } from 'react'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import Card from 'react-bootstrap/Card'
import Container from 'react-bootstrap/Container';
import { Col } from 'react-bootstrap';

function Signup() {
    const [windowWidth, setWidth] = useState(window.innerWidth);
    const [windowHeight, setHeight] = useState(window.innerHeight - 56);
    const [contSize, setContSize] = useState(0);

    useEffect(() => {
        if (window.innerWidth <= 1100 && window.innerWidth >= 800) {
            setContSize(window.innerWidth / 1)
        }
        else if (window.innerWidth > 1000) {
            setContSize(window.innerWidth / 3)
        }
        else {
            setContSize(window.innerWidth / 1)
        }
    });


    const changeSize = () => {
        setWidth(window.innerWidth);
        setHeight(window.innerHeight - 56);
    }

    window.addEventListener("resize", changeSize)

    return (
        <div className="row d-flex justify-content-center" style={{ backgroundColor: '#17223B', height: windowHeight, width: windowWidth, }}>
            <div style={{ backgroundColor: 'white', width: contSize, alignItems: 'center' }} >
                <Form style={{ backgroundColor: 'red', top: 50 }}>
                    <Form.Group controlId="email">
                        <Form.Control style={{}} type="text" placeholder='Username' />
                    </Form.Group>
                    {/* <Form.Group controlId="email">
                                <Form.Control type="email" placeholder="Enter email" />
                            </Form.Group>

                            <Form.Group controlId="phoneNumber">
                                <Form.Control type="email" placeholder='Phone number' />
                            </Form.Group>

                            <Form.Group controlId="password">
                                <Form.Control type="password" placeholder="Password" />
                            </Form.Group>

                            <Button variant="primary" type="submit">
                                Submit
                        </Button> */}
                </Form >
            </div >
        </div >
    );
}

export default Signup;
